mapper.update:
UPDATE 
	approval
SET 
	approval_status = #{approvalStatus}

WHERE
	approval_id = #{approvalId}

UPDATE approval_line
SET line_status = #{status}
WHERE line_id = #{lineId}

mapper.select:
    SELECT *
	FROM approval_line
	WHERE approval_id = #{approvalId}
  	AND approver_user_no = #{userId} AND line_status = #{status}    
  	ORDER BY line_order

SELECT *
FROM approval_line
WHERE approval_id = #{approvalId}
  AND line_order = #{lineOrder}

SELECT approval_id, user_no, approval_title, approval_type, approval_status, start_date FROM approval WHERE user_no = #{userNo}
AND approval_title LIKE '%' || #{keyword} || '%'
ORDER BY start_date DESC

SELECT USER_NAME 
FROM MEMBER
WHERE USER_NO = #{userNo}
