mapper.update:
UPDATE approval_line
SET line_status = #{lineStatus}
WHERE line_id = #{lineId}

mapper.select:
SELECT *
FROM approval_line
WHERE approval_id = #{approvalId}
  AND approval_no = #{approverNo}
  AND line_status = #{lineStatus}
ORDER BY line_order

SELECT *
FROM approval_line
WHERE approval_id = #{approvalId}
  AND line_order = #{lineOrder}
