mapper.select:
SELECT USER_NO
FROM MEMBER
WHERE EMAIL = #{email}

SELECT *
FROM MEMBER
WHERE USER_NAME = #{userName} AND EMAIL = #{email}

mapper.update:
UPDATE MEMBER
SET USER_PWD = #{encodedPwd}
WHERE EMAIL = #{email}

UPDATE MEMBER
SET PHONE = #{string}
WHERE USER_NO = #{userNo}

UPDATE MEMBER
SET ADDRESS = #{address},
	ADDRESS_DETAIL = #{addressDetail}
WHERE USER_NO = #{userNo}

mapper.select:
SELECT AUTHORITY
FROM AUTHORITY
WHERE USER_NO = #{userNo}

SELECT IMAGE_CODE, CHANGE_NAME
FROM IMAGE
WHERE REF_ID = #{userNo}
