mapper.update:
UPDATE CALENDAR
   SET DELETED_YN = 'Y',
       UPDATE_DATE = SYSTIMESTAMP
 WHERE CAL_ID = #{calId}
   AND OWNER_USER_NO = #{ownerUserNo}
   AND DELETED_YN = 'N'

mapper.select:
WITH MY_ORG AS (
  SELECT DEPT_ID, POS_ID FROM USER_HR WHERE USER_NO = #{userNo}
)
SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END AS hasAccess
FROM (
  SELECT 1
  FROM CALENDAR c
  LEFT JOIN CALENDAR_MEMBER cm  ON cm.CAL_ID = c.CAL_ID AND cm.USER_NO = #{userNo}
  LEFT JOIN MY_ORG mo           ON 1=1
  LEFT JOIN CALENDAR_DEPT_SHARE cds ON cds.CAL_ID = c.CAL_ID AND cds.DEPT_ID = mo.DEPT_ID
  LEFT JOIN CALENDAR_POS_SHARE  cps ON cps.CAL_ID = c.CAL_ID AND cps.POS_ID  = mo.POS_ID
  WHERE c.CAL_ID = #{calId}
    AND c.DELETED_YN = 'N'
    AND ( c.OWNER_USER_NO = #{userNo}
       OR cm.USER_NO IS NOT NULL
       OR cds.DEPT_ID IS NOT NULL
       OR cps.POS_ID  IS NOT NULL )
)
