mapper.select:
SELECT COUNT(1)
FROM EVENT
WHERE ROOM_ID = #{roomId}
  AND STATUS IN ('CONFIRMED','TENTATIVE')
  AND START_AT < #{endAt}
  AND END_AT   > #{startAt}

SELECT COUNT(1)
FROM EVENT
WHERE ROOM_ID = #{roomId}
  AND STATUS IN ('CONFIRMED','TENTATIVE')
  AND START_AT < #{endAt}
  AND END_AT   > #{startAt}
  AND EVENT_ID <> #{eventId}

mapper.insert:
INSERT INTO EVENT_PARTICIPANT (EP_ID, EVENT_ID, USER_NO, KIND)
SELECT EVENT_PARTICIPANT_SEQ.NEXTVAL, #{eventId}, #{userNo}, #{kind}
  FROM DUAL
 WHERE NOT EXISTS (
       SELECT 1 FROM EVENT_PARTICIPANT
        WHERE EVENT_ID = #{eventId}
          AND USER_NO  = #{userNo}
          AND KIND     = #{kind}
 )

mapper.update:
UPDATE EVENT_TYPE
   SET TYPE_NAME = #{typeName}
 WHERE TYPE_ID = #{typeId}

UPDATE LABEL
   SET LABEL_NAME = #{req.labelName},
       LABEL_COLOR = #{req.labelColor}
 WHERE LABEL_ID = #{labelId}

mapper.select:
SELECT e.EVENT_ID, e.CAL_ID, e.TITLE, e.START_AT, e.END_AT, e.ALL_DAY_YN, e.LOCATION_TEXT, e.NOTE, e.ROOM_ID AS E_ROOM_ID, -- 👈 이벤트의 ROOM_ID는 E_ROOM_ID로 별도 별칭 e.STATUS, e.LABEL_ID, e.TYPE_ID, e.CREATE_BY_USER_NO,
r.ROOM_ID AS ROOM_ID, -- 👈 회의실 요약 맵에서 쓰는 ROOM_ID는 r.* 컬럼 r.ROOM_NAME AS ROOM_NAME, r.BUILDING_NAME AS BUILDING_NAME, r.FLOOR AS FLOOR, r.ROOM_NO AS ROOM_NO, r.CAPACITY AS CAPACITY, r.DETAIL_LOCATION AS DETAIL_LOCATION
FROM EVENT e /* 👇 최신/유효한 예약 1건만 골라 이벤트별로 룸을 매칭 (테이블명/컬럼명은 프로젝트 실제 명칭에 맞춰주세요) */ LEFT JOIN ( SELECT EVENT_ID, MAX(ROOM_ID) KEEP (DENSE_RANK LAST ORDER BY RESV_ID) AS ROOM_ID FROM ROOM_RESERVATION WHERE STATUS = 'CONFIRMED' GROUP BY EVENT_ID ) rr ON rr.EVENT_ID = e.EVENT_ID
/* EVENT.ROOM_ID가 있으면 그걸 우선, 없으면 예약의 ROOM_ID 사용 */ LEFT JOIN CONFERENCE_ROOM r ON r.ROOM_ID = NVL(e.ROOM_ID, rr.ROOM_ID)
WHERE e.EVENT_ID = #{value}
