mapper.select:
SELECT
  EVENT_ID, CAL_ID, TITLE, START_AT, END_AT, ALL_DAY_YN,
  LOCATION_TEXT, NOTE, ROOM_ID, STATUS, LABEL_ID, EVENT_TYPE,
  RRULE, EXDATES, CREATE_BY_USER_NO, CREATE_DATE, UPDATE_USER_NO, UPDATE_DATE
FROM EVENT
WHERE CAL_ID = #{calendarId}
  AND START_AT < #{to}
  AND END_AT   > #{from}
  AND STATUS   <> 'DELETED'
ORDER BY START_AT ASC, END_AT ASC, EVENT_ID ASC

SELECT COUNT(1)
FROM EVENT
WHERE ROOM_ID = #{roomId}
  AND STATUS IN ('CONFIRMED','TENTATIVE')
  AND START_AT < #{endAt}
  AND END_AT   > #{startAt}

SELECT COUNT(1)
FROM EVENT
WHERE ROOM_ID = #{roomId}
  AND STATUS IN ('CONFIRMED','TENTATIVE')
  AND START_AT < #{endAt}
  AND END_AT   > #{startAt}
  AND EVENT_ID <> #{eventId}

mapper.insert:
INSERT INTO EVENT_PARTICIPANT (EP_ID, EVENT_ID, USER_NO, KIND)
SELECT EVENT_PARTICIPANT_SEQ.NEXTVAL, #{eventId}, #{userNo}, #{kind}
  FROM DUAL
 WHERE NOT EXISTS (
       SELECT 1 FROM EVENT_PARTICIPANT
        WHERE EVENT_ID = #{eventId}
          AND USER_NO  = #{userNo}
          AND KIND     = #{kind}
 )

mapper.update:
UPDATE LABEL
   SET LABEL_NAME = #{req.labelName},
       LABEL_COLOR = #{req.labelColor}
 WHERE LABEL_ID = #{labelId}
