mapper.select:
SELECT DEP_ID
FROM DEPARTMENT
WHERE DEP_NAME = #{depName}

SELECT POS_ID
FROM POSITIONS
WHERE POS_NAME = #{posName}

SELECT USER_NO, USER_NAME, EMAIL, HIRE_DATE, PHONE, EXTENSION, ADDRESS, ADDRESS_DETAIL, UPDATE_DATE, DEP_NAME, POS_NAME, STATUS FROM MEMBER LEFT JOIN DEPARTMENT USING(DEP_ID) LEFT JOIN POSITIONS USING(POS_ID)
STATUS = #{status}
AND USER_NAME LIKE '%' || #{userName} || '%'
AND DEP_ID IN ( SELECT DEP_ID FROM DEPARTMENT START WITH DEP_NAME = #{depName} CONNECT BY PRIOR DEP_ID = PARENT_ID )
AND POS_NAME = #{posName}
ORDER BY USER_NO DESC
